    //超时
    public static boolean wordBreak(String s, List<String> wordDict) {
        if (s.equals("")) {
            return true;
        }

        for (int i = 0; i < wordDict.size(); i++) {
            String dic = wordDict.get(i);
            int index = s.indexOf(dic);
            if (index >= 0) {
                String left = s.substring(0, index);
                boolean isLeft = wordBreak(left, wordDict);
                String right = s.substring(index + dic.length(), s.length());
                boolean isRight = wordBreak(right, wordDict);

                if (isLeft && isRight) {
                    return true;
                }
            }
        }
        return false;
    }

    //超时
    public static boolean wordBreak2(String s, List<String> wordDict) {
        if (s.equals("")) {
            return true;
        }
        for (String dic : wordDict) {
            if (s.indexOf(dic) >= 0) {
                s = s.replace(dic, "");
                return wordBreak(s, wordDict);
            }
        }
        return false;
    }

    /**
     * 动态规划
     * 类似于背包问题
     *
     * @param s
     * @param wordDict
     * @return
     */
    public static boolean wordBreak3(String s, List<String> wordDict) {
        Set<String> wordDictSet = new HashSet(wordDict);
        boolean[] dp = new boolean[s.length() + 1];
        dp[0] = true;
        for (int i = 1; i <= s.length(); i++) {
            for (int j = 0; j < i; j++) {
                if (dp[j] && wordDictSet.contains(s.substring(j, i))) {
                    dp[i] = true;
                    break;
                }
            }
        }
        return dp[s.length()];
    }
